import React from "react";

import DisplayData from "../DisplayData/Displaydata";

const DispDistrictdata = ({ selValue, data, sortby, orderby }) => {
  var transformedData = [];
  var SortedArray = [];
  const districtsData = data[selValue].districtData;
  const DistrictNames = Object.keys(districtsData);

  Object.keys(districtsData).map((key) => {
    transformedData = [...transformedData, data[selValue].districtData[key]];
  });
  if (orderby === "Desc") {
    SortedArray = transformedData.sort((a, b) => {
      if (a[sortby] > b[sortby]) return -1;
      if (a[sortby] < b[sortby]) return 1;
      else return 0;
    });
  } else {
    SortedArray = transformedData.sort((a, b) => {
      if (a[sortby] > b[sortby]) return 1;
      if (a[sortby] < b[sortby]) return -1;
      else return 0;
    });
  }

  const list = SortedArray.map((key, i) => {
    return <DisplayData DistName={DistrictNames[i]} data={key} />;
  });

  // const list = Object.keys(districtsData).map((key) => {
  //   return (
  //     <DisplayData DistName={key} data={data[selValue].districtData[key]} />
  //   );
  // });

  return <div className="row mx-md-n5">{list}</div>;
};

export default DispDistrictdata;
